import React, { Component } from 'react';
import axios from 'axios';
import CryptoJS from "react-native-crypto-js";
import { codes } from "../secrets/secrets.js";
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import AccessDenied from './modules/AccessDenied';
import { authService } from './modules/authService';
import '../css/add-user.css';

export default class CreateUser extends Component {
    
    constructor(props) {
        super(props);
        this.onChangeUserEmail = this.onChangeUserEmail.bind(this);
        this.onChangeUserPassword = this.onChangeUserPassword.bind(this);
        this.changeUserRole = this.changeUserRole.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.toggle = this.toggle.bind(this);
        this.state = {
            user_email: '',
            user_password: '',
            user_role: 'Role',
            dropdownOpen: false,
			currentUser: authService.currentUserValue
        }
    }

    toggle() {
        this.setState(prevState => ({
          dropdownOpen: !prevState.dropdownOpen
        }));
      }

    onChangeUserEmail(e) {
        this.setState({
            user_email: e.target.value.toLowerCase()
        });
    }
    
    onChangeUserPassword(e) {
        this.setState({
            user_password: e.target.value
        });
    }

    changeUserRole(e) {
        this.setState({
            user_role: e.currentTarget.textContent
        });
    }
    
    onSubmit(e) {
        e.preventDefault();
        
        console.log(`Form submitted:`);
        console.log(`User Email: ${this.state.user_email}`);
        console.log(`User role: ${this.state.user_role}`)
        
        var email = CryptoJS.AES.encrypt(this.state.user_email, codes.staff, {iv: codes.iv});
        var pass  = CryptoJS.AES.encrypt(Math.random().toString(36).slice(-8), codes.staffPass);

        var newUser = {
            email: email.toString(),
            password: pass.toString(),
            role: this.state.user_role.toLowerCase()
        };
        
        console.log(newUser)

        if(newUser.role == "role"){
            alert("You must select a role")
        }
        else{
            axios.post('http://'+process.env.REACT_APP_AWS_IP+':4000/admin/addUser', newUser)
            .then( (response) => {console.log(response);
                                if(response.status == 205){
                                    console.log("dupe email");
                                    alert("Email already in use");
                                 }
                                 else{
                                    console.log("else");
                                    axios.post('http://'+process.env.REACT_APP_AWS_IP+':4000/admin/send-email-staff', {email: email.toString()})
                                    .then( (response) => {console.log(response.data)
									                      this.props.history.push('/admin');
														  window.location.reload();
														 });
                                 }
            })
        }
    }
    
   render() {
	   if(this.state.currentUser.token.role !=='admin'){
	   return (
	   <AccessDenied/>
	   );
	  }else{
        return (
            <div className="createUser" style={{marginLeft: 100, marginRight: 100}}>
                
                <form className="addForm" onSubmit={this.onSubmit}>
                    <h3>Add User</h3>
                    <div className="form-group">
                        <label>Email: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.user_email}
                                onChange={this.onChangeUserEmail}
                                required/>
                        <br></br>
                        <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} required>
                        <DropdownToggle caret>
                        {this.state.user_role}
                        </DropdownToggle>
                        <DropdownMenu>
                        <DropdownItem>
                            <div onClick={this.changeUserRole}>
                                Recruiter
                            </div>
                        </DropdownItem>
                        <DropdownItem>
                            <div onClick={this.changeUserRole}>
                                Finance
                            </div>
                        </DropdownItem>
                        </DropdownMenu>
                        </Dropdown>
                    <br></br>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Add User" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
   }
}
